INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('粉丝蒸鸡腿','164.jpg','菌类','微辣',1,'鸡边腿、粉丝、盐、鸡精、白糖、胡椒粉、味极鲜、酱油、蚝油、料酒、玉米淀粉 
