INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('红枣焖栗子鸡','200.jpg','菌类','微辣',1,'鸡腿、红枣、栗子、生抽、料酒、花椒粉、盐、葱、姜、糖、油、红椒。','1.冬
