INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('蒜苗炒肉丝','152.jpg','蔬菜','咸香',4,'蒜苗、里脊肉、香菇、料酒、盐、生抽、蚝油、白胡椒粉、姜丝。','1.冬瓜和肉馅
