INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('油渣大白菜','152.jpg','甜点','微酸',4,'大白菜、油渣、猪油、料酒、老抽、生抽、豆瓣酱、盐、鸡精、葱。','1.虾仁开背
