INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('炒豆角米','141.jpg','蔬菜','咸香',2,'豆角米、辣椒、盐、蚝油、鸡精。','1.冬瓜和肉馅准备好.;2.肉馅中倒入适量蚝油，
