INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('紫薯花式面包','181.jpg','川菜','香甜',2,'高筋面粉、低筋面粉、鸡蛋液、白糖、牛奶、安琪酵母、紫薯泥、黄油。','1.虾
