INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('苦荞棉花蛋糕','151.jpg','蔬菜','苦',1,'鸡蛋、牛奶、新良苦荞蛋糕粉、玉米油、细砂糖、柠檬汁。','1.玉米清理干净;2. 
