INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('芒果夹心草莓蛋糕','173.jpg','夜宵','微咸',5,'鸡蛋、低筋面粉、砂糖、牛奶、玉米油、柠檬汁、香草精、奶油、糖、芒果 
