INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('海米拌黄瓜','154.jpg','夜宵','中辣',0,'黄瓜、海米、盐、糖、大蒜、红椒、香菜、橄榄油、黑胡椒。','1.冬瓜和肉馅准备
