INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('酱拌茄子','104.jpg','夜宵','香咸',3,'茄子、小葱、大蒜、豆瓣酱。','1.玉米清理干净;2.冬瓜去掉瓤，洗干净不用去皮切
