INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('东北大拌菜','173.jpg','江苏菜','微苦',4,'土豆、茄子、香葱、香菜、豆瓣酱、荔枝、玉米饼。','1.虾仁开背，挑去虾线清
