INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('莴苣木耳拌鸡丝','146.jpg','甜点','酸甜',2,'鸡脯肉、莴苣、黑木耳、姜、料酒、味极鲜、红椒、香醋、油、盐。','1.准备
