INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('剁椒香菜拌茄条','193.jpg','午餐','微甜',1,'长条茄、剁椒、香菜、蒜、生抽、醋、油、盐。','1.冬瓜去皮去籽，切条，大
