INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('凉拌豇豆','156.jpg','汤羹','微酸',5,'豇豆、西红柿、老姜、大蒜、白糖、芝麻油、盐。','1.玉米清理干净;2.冬瓜去掉瓤 
