INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('棒棒鸡丝','146.jpg','鲁菜','咸香',1,'鸡胸肉、花生碎、葱丝、姜末、蒜末、芝麻酱、辣椒油、香油、生抽、醋、白糖、精 
