INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('香蕉饼','151.jpg','徽菜','香甜',1,'手抓饼、香蕉、鹌鹑蛋、生菜。','1.虾仁开背，挑去虾线清洗干净，加盐、料酒、胡椒
