INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('懒人五彩蒸饭','190.jpg','川菜','微咸',4,'大米、青豆、玉米粒、香肠、香菇、红萝卜、葱花、生抽、盐、蚝油、白胡椒粉 
