INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('酱香小花卷','124.jpg','菌类','酸甜',5,'普通面粉、酵母粉、水、葱、黄豆酱、味极鲜酱油。','1.冬瓜和肉馅准备好.;2.肉
