INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('南瓜糖三角','157.jpg','江苏菜','微酸',4,'面粉、南瓜、红糖、酵母、清水。','1.虾仁开背，挑去虾线清洗干净，加盐、料
