INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('黄桃酱','185.jpg','徽菜','微辣',2,'黄桃、白沙糖。','1.冬瓜去皮去籽，切条，大蒜一把。;2.热锅温油，蒜瓣炝锅。;3.冬
