INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('香菇玉米粥','140.jpg','夜宵','苦',5,'糯米、香菇、菠菜、玉米粒、豌豆、水、盐。','1.玉米清理干净;2.冬瓜去掉瓤，洗 
