INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('卤肉饭','171.jpg','晚餐','酸甜',0,'五花肉、干香菇、水煮蛋、青菜、洋葱、蒜头、大料、盐、生抽、老抽、冰糖。','1.冬
