INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('桃子酱','174.jpg','午餐','清淡',2,'水蜜桃、白砂糖、柠檬。','1.玉米清理干净;2.冬瓜去掉瓤，洗干净不用去皮切成薄片 
