INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('煎猪扒配酸甜椒','166.jpg','菌类','爆辣',4,'猪扒、甜椒、洋葱、盐、胡椒、红酒醋、罗勒叶、百里香、蒜。','1.玉米清理
