INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('劲蒜安格斯牛肉粒','184.jpg','粤菜','咸香',4,'安格斯牛肉、独头大蒜、红黄绿彩椒、口蘑、盐、胡椒粉、芥末膏、鸡粉、 
