INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('燕麦花生露豆渣比萨','115.jpg','晚餐','微甜',3,'花生、燕麦、面粉、鸡蛋、水、青红椒、洋葱、培根、马苏里啦奶酪、盐 
