INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('芦笋牛肉卷','189.jpg','徽菜','微苦',5,'牛肉、芦笋、蜂蜜、黄芥末酱、蛋黄酱、盐、黑胡椒、葵花籽油。','1.准备的原料
