INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('芒果乳酪三明治','140.jpg','蔬菜','麻辣',2,'鲜奶酪、面包片、芒果。','1.所需食材准备就绪;2.冬瓜去皮切块;3.番茄改刀
