INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('意大利青酱面','186.jpg','川菜','酸甜',0,'罗勒、松仁、橄榄油、盐、大蒜、帕玛森干酪、虾、意大利面。','1.准备、冬瓜
