INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('集果沙拉','144.jpg','午餐','微苦',5,'哈密瓜、青瓜、红萝卜、生菜、玉米、香肠、沙拉酱。','1.虾仁开背，挑去虾线清洗
