INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('蒜香开边蒸龙虾','172.jpg','浙江菜','麻辣',3,'熟冻加拿大波士顿龙虾、大蒜、豆芽、香葱、盐、黄油。','1.玉米清理干净
