INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('焗龙虾','155.jpg','早饭','苦',3,'龙虾、玉米粉、黄油、蒜片。','1.冬瓜和肉馅准备好.;2.肉馅中倒入适量蚝油，白胡椒粉
