INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('麻辣烫','138.jpg','川菜','苦',0,'麻辣面、黄瓜、银耳。','1.冬瓜洗净，去皮，切片。;2.盐渍海带提前浸泡一晚，去掉咸 
