INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('香辣拌面酱','183.jpg','早饭','咸香',3,'肉末、海天黄豆酱、洋葱、红尖椒、香菜、油、姜、葱、蒜、鸡粉、白糖、料酒、 
