INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('船点鸭子','169.jpg','徽菜','麻辣',4,'澄粉、糯米粉、豆沙、胡萝卜、色拉油、黑芝麻、蛋清。','1.准备、冬瓜去皮洗净切
