INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('花生酱','195.jpg','蔬菜','微辣',5,'花生、芝麻、花生油、白糖。','1.玉米清理干净;2.冬瓜去掉瓤，洗干净不用去皮切成 
