INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('四川霉豆腐','179.jpg','浙江菜','微辣',1,'老豆腐、花椒面、白胡椒粉、辣椒面、盐、高度粮食白酒。','1.所需食材准备就
