INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('熬葱油','117.jpg','川菜','微苦',0,'香葱、食用植物油。','1.虾仁开背，挑去虾线清洗干净，加盐、料酒、胡椒粉、少许淀
