INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('自制披萨番茄酱','192.jpg','晚餐','爆辣',2,'西红柿、大蒜、黄油、洋葱、盐、黑胡椒粉、糖、披萨草。','1.冬瓜洗净，去
