INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('自制披萨酱','188.jpg','川菜','清淡',5,'番茄、淀粉水、玉米油、高汤、洋葱末、大蒜末、番茄沙司、黑胡椒粉。','1.虾仁
