INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('转化糖浆','200.jpg','鲁菜','酸甜',0,'细砂糖、水、新鲜柠檬汁。','1.冬瓜洗净，去皮，切片。;2.盐渍海带提前浸泡一晚 
