INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('芝麻花生脆','123.jpg','江苏菜','微咸',3,'花生米、鸡蛋、面粉、熟芝麻、白糖、水。','1.冬瓜和肉馅准备好.;2.肉馅中倒
