INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('炸鸡翅','149.jpg','粤菜','微苦',3,'鸡翅、新奥尔良烤肉料、普通面粉、盐、鸡蛋。','1.所需食材准备就绪;2.冬瓜去皮切 
