INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('腌黑腊八豆','164.jpg','晚餐','微酸',1,'黑豆、盐、生姜、干辣椒。','1.虾仁开背，挑去虾线清洗干净，加盐、料酒、胡椒
