INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('酸辣汤年糕','117.jpg','甜点','香咸',5,'年糕、番茄、番茄酱、韩式辣酱、精盐、食用油。','1.玉米清理干净;2.冬瓜去掉 
