INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('培根风琴土豆','185.jpg','蔬菜','咸香',3,'土豆、培根、蜜汁烤肉酱。','1.虾仁开背，挑去虾线清洗干净，加盐、料酒、胡
