INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('鸡汁冬茸汤','137.jpg','浙江菜','微酸',2,'冬瓜、蛋清、葱、姜、鸡汁、芝麻油、水淀粉、枸杞、盐。','1.准备、冬瓜去皮
