INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('银耳红枣桂圆汤','150.jpg','徽菜','微苦',3,'银耳、桂圆、和田红枣、冰糖。','1.玉米清理干净;2.冬瓜去掉瓤，洗干净不 
