INSERT INTO dishes(dishes_name,dishes_pic,category_name,taste,score,Material,steps) VALUES('三文鱼薯饼','142.jpg','鲁菜','微辣',2,'三文鱼、土豆、柠檬、盐、胡椒粉、橄榄油、酸奶。','1.玉米清理干净;2.冬瓜去 
